INSERT INTO `product` VALUES ('487754-303', '2020-04-13 15:30:12', 3.90, 'Nike Air Tailwind 79', 79.95, 0.00, 'Nike', 'Making its debut in 1978 for the Honolulu Marathon, the Nike Air Tailwind was a revolution in cushioning. Now, a racing icon returns with the Nike Air Tailwind 79, featuring nearly original materials and colours, plus the Nike Air unit that made it a winner.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/05c53959-ad04-40c5-b7b0-dec0648a8cb2/air-tailwind-79-shoe-Psjn7n.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/26faca36-dfe6-4126-8011-9f8430e7e3a7/air-tailwind-79-shoe-Psjn7n.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5e32af2c-eb11-42cf-ae11-64152f676ffb/air-tailwind-79-shoe-Psjn7n.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/8d067c58-0c05-41d6-9691-15518b158b82/air-tailwind-79-shoe-Psjn7n.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/33c5c25e-6a96-4f78-ad25-cba323d7e3cf/air-tailwind-79-shoe-Psjn7n.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ac3d3267-f46f-4daf-870b-7c8f20f50714/air-tailwind-79-shoe-Psjn7n.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5c1df95f-da9e-4a81-9d4e-51600e58af7b/air-tailwind-79-shoe-Psjn7n.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/9b4677bf-5402-4487-89f8-a63aab70ff98/air-tailwind-79-shoe-Psjn7n.jpg\"]', 1, 0);
