INSERT INTO `product` VALUES ('528895-003', '2020-04-13 15:32:32', 4.40, 'Air Jordan 11 Retro Low', 118.97, 0.00, 'Nike', 'Since bursting onto the basketball scene in 1996, the Air Jordan 11 Retro Low Men\'s Shoe has been one of the most beloved Jordan designs ever. Signature details and Nike Air cushioning combine for the ultimate fusion of style and comfort.', '[\"https://c.static-nike.com/a/images/t_PDP_1728_v1/mraaehf0e5xklwt1fde4/air-jordan-11-retro-low-shoe-Xnln0p.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/l3ejo6vyzhu8fpsorcxz/air-jordan-11-retro-low-shoe-Xnln0p.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/gpgnbilzp2kaesqgsezi/air-jordan-11-retro-low-shoe-Xnln0p.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/ay6ox375b7yeg7zggogj/air-jordan-11-retro-low-shoe-Xnln0p.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/csbeksanzo6gdmfhp6le/air-jordan-11-retro-low-shoe-Xnln0p.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/rigkpcbbjoqhxud9lg1j/air-jordan-11-retro-low-shoe-Xnln0p.jpg\"]', 1, 0);
