INSERT INTO `product` VALUES ('634835-108', '2020-04-13 15:34:28', 4.50, 'Nike Air Huarache', 89.95, 0.00, 'Nike', 'Designed to stretch with your foot, the Nike Air Huarache Women\'s Shoe has remained a sneaker staple since its 1991 debut, when it changed the face of running footwear forever.', '[\"https://c.static-nike.com/a/images/t_PDP_1728_v1/r4rbe0wqytas2utewhs9/air-huarache-shoe-2kvnqX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/f0dnxzfc5nqptfaeqte7/air-huarache-shoe-2kvnqX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/cg6hkorcytmtesgdkdon/air-huarache-shoe-2kvnqX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/k6ntefnhvguoqzvcjogj/air-huarache-shoe-2kvnqX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/m9si6q0v81q9bdxjs0no/air-huarache-shoe-2kvnqX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/mlnu2ofatyky1xuvrd8o/air-huarache-shoe-2kvnqX.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('640744-016', '2020-04-13 15:30:30', 4.10, 'Nike Air Max 98', 101.97, 0.00, 'Nike', 'The Air Max 98 rode the wave of its legendary predecessor, debuting with full-length visible Air and fluid design lines. Now it returns, sporting the same signature look and a new run of colours.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/n97xahynyhuvjf2dpnou/air-max-98-shoe-kW5Qfw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/wawrf56owntlwicfuzrh/air-max-98-shoe-kW5Qfw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/l0delh1nc7suakaa2rz2/air-max-98-shoe-kW5Qfw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cfpz6iq3exqqku4ibqf1/air-max-98-shoe-kW5Qfw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/yanloqp5grl3xtn0zbrd/air-max-98-shoe-kW5Qfw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e2jkdtgrji4yklrosyny/air-max-98-shoe-kW5Qfw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a9whmiwnatbbr56zdoea/air-max-98-shoe-kW5Qfw.jpg\"]', 1, 0);
