INSERT INTO `product` VALUES ('AO0354-108', '2020-04-13 15:30:13', 4.90, 'Nike Zoom 2K', 69.95, 0.00, 'Nike', 'Inspired by running silhouettes from the early 2000s, the Nike Zoom 2K is reimagining the golden age of running sneakers. A classic Zoom bag takes it back to where it began, while the rubber wings that wrap the outsole push the design into the future.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-6be36dcc-4a99-4221-8e43-6a8cb88d433e/zoom-2k-shoe-Wsjtwq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-03641c7d-4917-4eb8-af35-f7bb34d85222/zoom-2k-shoe-Wsjtwq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-e094a982-664e-4a96-9b5d-a12da3502c6e/zoom-2k-shoe-Wsjtwq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-82e9e52d-7190-4ce2-913a-70cc03f150c7/zoom-2k-shoe-Wsjtwq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-707369d1-9207-4eb3-9805-d7e1b7a485f9/zoom-2k-shoe-Wsjtwq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-9830d89e-0d18-47ee-9ba4-fb0b49ce22e8/zoom-2k-shoe-Wsjtwq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-da69c722-8dc8-4274-8e04-7197bec84afa/zoom-2k-shoe-Wsjtwq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-25d7e431-1a8b-4dae-93ab-acbf2ecc487c/zoom-2k-shoe-Wsjtwq.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AO0566-606', '2020-04-13 15:28:18', 2.80, 'Nike Phantom Venom Academy FG', 74.95, 0.00, 'Nike', 'The Nike Phantom Venom Academy FG is engineered for powerful, precise strikes that win games. Ridges on the instep create spin to control the flight of the ball, while the firm-ground plate provides the traction needed to unleash at any moment.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/c1b6a564-a475-46f3-acab-8076ecfca48c/phantom-venom-academy-fg-football-boot-8K8CNP.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fbf55013-d88d-4626-8e3c-d2360ba0159a/phantom-venom-academy-fg-football-boot-8K8CNP.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/6d64a679-51b7-46db-9499-fd906a8fad2c/phantom-venom-academy-fg-football-boot-8K8CNP.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/84345e2c-ef3f-4305-93a0-17e749e5ee83/phantom-venom-academy-fg-football-boot-8K8CNP.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/3427842d-2a42-44d9-b6ec-41c14acad6cd/phantom-venom-academy-fg-football-boot-8K8CNP.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/f7398cf0-0222-4ab9-874c-f2d9ca6393af/phantom-venom-academy-fg-football-boot-8K8CNP.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/d932a2d9-5cb5-428c-ae18-89cb45f37110/phantom-venom-academy-fg-football-boot-8K8CNP.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/3e560d17-8e81-4f46-a1d7-a5c0b4f79127/phantom-venom-academy-fg-football-boot-8K8CNP.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/97284cb6-7c63-44ab-a9e8-1324503a0750/phantom-venom-academy-fg-football-boot-8K8CNP.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AO0570-810', '2020-04-13 15:30:26', 0.00, 'Nike Phantom Venom Academy IC', 52.47, 0.00, 'Nike', 'The Nike Phantom Venom Academy IC is engineered for powerful, precise strikes that win games. Ridges on the instep create spin to control the flight of the ball, while the herringbone outsole provides the traction needed to unleash at any moment.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/rt6wjzglgt0giz6cu3em/phantom-venom-academy-ic-indoor-court-football-boot-hlhp45.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ljm5takrbtmrdfkzwtpk/phantom-venom-academy-ic-indoor-court-football-boot-hlhp45.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vk5eilhgwdjtfr6rgrrh/phantom-venom-academy-ic-indoor-court-football-boot-hlhp45.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/iuo3jcaidjenn8i52hs6/phantom-venom-academy-ic-indoor-court-football-boot-hlhp45.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/u1lz6amh3lacof3pbq9u/phantom-venom-academy-ic-indoor-court-football-boot-hlhp45.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/gdjyvdxisdaskbabs8ng/phantom-venom-academy-ic-indoor-court-football-boot-hlhp45.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AO0571-606', '2020-04-13 15:34:40', 5.00, 'Nike Phantom Venom Academy TF', 74.95, 0.00, 'Nike', 'The Nike Phantom Venom Academy TF is engineered for powerful, precise strikes that win games. Ridges on the instep help create spin to control the flight of the ball. The sole provides the turf traction needed to unleash at any moment.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/9ba6a211-6218-42d2-a095-75c74f168fc5/phantom-venom-academy-tf-football-shoe-2RWKVD.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/0f808fc0-cd48-4307-92d4-e31869c6a5ab/phantom-venom-academy-tf-football-shoe-2RWKVD.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c9931f10-1ad5-41f1-aadd-9d58de2ac2d2/phantom-venom-academy-tf-football-shoe-2RWKVD.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/4147cf6f-98f2-4900-964b-40bdf932f090/phantom-venom-academy-tf-football-shoe-2RWKVD.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/dda50506-0362-4f2c-a01b-91a9bc5cba95/phantom-venom-academy-tf-football-shoe-2RWKVD.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/315aea40-ed52-4f11-a3ab-340a39acb3d9/phantom-venom-academy-tf-football-shoe-2RWKVD.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5729eddb-379b-41e2-aea3-a6cb1a592895/phantom-venom-academy-tf-football-shoe-2RWKVD.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/52b773dc-f32b-4172-9bb2-f17662e0871f/phantom-venom-academy-tf-football-shoe-2RWKVD.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c8e20e09-cd46-4491-9ee9-fdc6c90a693d/phantom-venom-academy-tf-football-shoe-2RWKVD.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AO0577-717', '2020-04-13 15:32:28', 0.00, 'Nike PhantomVNM Club FG', 34.97, 0.00, 'Nike', 'The Nike PhantomVNM Club FG is engineered for powerful, precise strikes that win games. A microtexture on the soft synthetic upper provides ball control, while the firm-ground plate provides the traction needed to unleash at any moment.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/okjswezbkmum2dspmy1v/phantomvnm-club-fg-football-boot-dtCFTs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/uev6ticnxqbxm8et9flh/phantomvnm-club-fg-football-boot-dtCFTs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/eal9vsaslaqswcioz4zr/phantomvnm-club-fg-football-boot-dtCFTs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ejv5f5l4oezq9xxn5pxf/phantomvnm-club-fg-football-boot-dtCFTs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/mqr2lpgiw7gymdhiigtu/phantomvnm-club-fg-football-boot-dtCFTs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/xkznlqyx0tkbh4vqcldw/phantomvnm-club-fg-football-boot-dtCFTs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/klrf5bvm4fv9wsi3enkq/phantomvnm-club-fg-football-boot-dtCFTs.jpg\"]', 1, 0);
