INSERT INTO `product` VALUES ('AQ0114-101', '2020-04-13 15:32:41', 4.80, 'LeBron x John Elliott Icon', 199.95, 0.00, 'Nike', 'The LeBron x John Elliott Icon brings the clean and layered approach of a lifestyle shoe to a hardwood classic. Cushioning from the LeBron 8 combines with court-inspired details and translucent materials as a tribute to Elliott\'s favourite shoe in the series. The result: Power, unleashed.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/sjqv0sx5d3bpxteh5xgg/lebron-john-elliott-icon-shoe-DFX2xX.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zwisdzalff7t1fflkbyy/lebron-john-elliott-icon-shoe-DFX2xX.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/sapqdcn1wnbxpdkmgjiw/lebron-john-elliott-icon-shoe-DFX2xX.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/soecmx6pstfdefjggczm/lebron-john-elliott-icon-shoe-DFX2xX.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bmidlcplvbsfio7k4ane/lebron-john-elliott-icon-shoe-DFX2xX.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/agtuxdcmd3uj2g4q1u0y/lebron-john-elliott-icon-shoe-DFX2xX.jpg\"]', 1, 0);
