INSERT INTO `product` VALUES ('AQ0407', '2020-04-13 15:07:56', 0.00, 'MEN\'S ADIDAS RUNNING SOLAR DRIVE ST SHOES', 109.99, 0.00, 'Adidas', 'These running shoes are designed for stability and comfort. A breathable mesh upper moves with your foot, while the stability rail provides support. Boost cushioning and a flexible outsole work together to keep your stride smooth and energised. The heel counter locks in the fit while allowing the Achilles to move freely.', NULL, 1, 0);
