INSERT INTO `product` VALUES ('AR4225-003', '2020-04-13 15:32:20', 4.20, 'LeBron Soldier 13 SFG', 95.97, 0.00, 'Nike', 'LeBron works hard in the offseason, getting stronger and fine-tuning his already devastating skills. Inspired by his dedication and commitment to inclusivity, the Soldier 13 SFG combines LeBron-tested cushioning, containment and support with a wide, flexible collar and lace-free closure that lets all players get in and out of the shoe with ease.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/bm1w5fwwi6vejhupqhoa/lebron-soldier-13-sfg-basketball-shoe-6bjdsK.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/t0dxnl4s6o7beqkc4cpq/lebron-soldier-13-sfg-basketball-shoe-6bjdsK.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/pwhxmf5o2ed7rfjjzpxu/lebron-soldier-13-sfg-basketball-shoe-6bjdsK.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/crnhcmr3h4wslribovrc/lebron-soldier-13-sfg-basketball-shoe-6bjdsK.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/hvsknhgnmb7ejsv0baat/lebron-soldier-13-sfg-basketball-shoe-6bjdsK.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/pgjrr0pz7o5uygylxk41/lebron-soldier-13-sfg-basketball-shoe-6bjdsK.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/o3bsmg32zazc0um1xn4e/lebron-soldier-13-sfg-basketball-shoe-6bjdsK.jpg\"]', 1, 0);
