INSERT INTO `product` VALUES ('AR4347-600', '2020-04-13 15:38:54', 1.00, 'Nike Zoom Pegasus Turbo XX', 135.97, 0.00, 'Nike', 'Bridging performance and style, the Nike Zoom Pegasus Turbo XX Women\'s Running Shoe combines a full-length ZoomX midsole with an upper re-imagined exclusively for women. This feather-light translucent upper pops with a racing-stripe print that looks as fast as it feels, while Nike ZoomX foam delivers revolutionary responsiveness. A transparent, soft TPU overlay wraps your midfoot for the fit you need.', '[\"https://c.static-nike.com/a/images/t_PDP_1728_v1/go6y9fdinwqn9rrvsu7c/zoom-pegasus-turbo-xx-running-shoe-pCFSFC.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/iqwqzqc35e2r4ynwv1jv/zoom-pegasus-turbo-xx-running-shoe-pCFSFC.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/wgamzazc9hltxhwgrr67/zoom-pegasus-turbo-xx-running-shoe-pCFSFC.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/nearjr64e0zldqiozegc/zoom-pegasus-turbo-xx-running-shoe-pCFSFC.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/pngrmclylymrm6t5ij4b/zoom-pegasus-turbo-xx-running-shoe-pCFSFC.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/qdohfocsb2pqc2khz0uc/zoom-pegasus-turbo-xx-running-shoe-pCFSFC.jpg\"]', 1, 0);
