INSERT INTO `product` VALUES ('AR5149-005', '2020-04-13 15:34:51', 0.00, 'Air Jordan 11 Low', 135.97, 0.00, 'Nike', 'Since bursting onto the basketball scene in 1996, the Air Jordan 11 has been one of the most beloved Jordan designs ever. To celebrate the championship mentality, the Air Jordan 11 Low Women\'s Shoe features signature details and Nike Air cushioning for the ultimate fusion of style and comfort.', '[\"https://c.static-nike.com/a/images/t_PDP_1728_v1/aarkrcj5v5uobkrpv43i/air-jordan-11-low-shoe-WZLQ1c.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/qqrlnwr2ssd6u5gzaht5/air-jordan-11-low-shoe-WZLQ1c.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/e7ocacafuqdaqedzicsk/air-jordan-11-low-shoe-WZLQ1c.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/ss50yporn91tk9vkwyfw/air-jordan-11-low-shoe-WZLQ1c.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/vdtatf0n0u3w5qvuhxhq/air-jordan-11-low-shoe-WZLQ1c.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/ajgendclx6fzuwkrwktz/air-jordan-11-low-shoe-WZLQ1c.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/qxs8bw7vaph6jflec7vj/air-jordan-11-low-shoe-WZLQ1c.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/lyjgho9o31hi6vuwhi53/air-jordan-11-low-shoe-WZLQ1c.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/i4fillv6arfamua2dwtw/air-jordan-11-low-shoe-WZLQ1c.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/nmneldzxxon4ptvzp4il/air-jordan-11-low-shoe-WZLQ1c.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AR5339-002', '2020-04-13 15:36:40', 4.50, 'Nike Air Force 1 Sage Low', 89.95, 0.00, 'Nike', 'Taking both height and craft to new levels, the Nike Air Force 1 Sage Low features a platform midsole and a pared-down upper for a bold yet feminine look.', '[\"https://c.static-nike.com/a/images/t_PDP_1728_v1/z1udf3xwzc33vpaytjlz/air-force-1-sage-low-shoe-7l6djX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/vd32nssg7juggyifsn1a/air-force-1-sage-low-shoe-7l6djX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/sbxt7jhnzsk4uzdih2xw/air-force-1-sage-low-shoe-7l6djX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/wjp3cwgcebxukykeevlu/air-force-1-sage-low-shoe-7l6djX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/ssxcwiotlejvtocpnvpm/air-force-1-sage-low-shoe-7l6djX.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/kpqlmbgbsndbzilgeojn/air-force-1-sage-low-shoe-7l6djX.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AR5676-401', '2020-04-13 15:32:43', 4.20, 'Nike Air Zoom Pegasus 36 Trail', 109.95, 0.00, 'Nike', 'An icon hits the paths less travelled in the Nike Air Zoom Pegasus 36 Trail. Perforated mesh upper offers breathable comfort, and double Zoom Air units cushion your stride. Outsole lugs optimise traction when running uphill.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-209abfbb-2949-4c49-8a4d-f56449e2be7e/air-zoom-pegasus-36-trail-trail-running-shoe-Kb3t4l.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-df0d5f1e-44ba-494b-aa73-720ef41dcd21/air-zoom-pegasus-36-trail-trail-running-shoe-Kb3t4l.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-c410b7fd-2201-4033-b8d0-f264c4d2760d/air-zoom-pegasus-36-trail-trail-running-shoe-Kb3t4l.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-33b0a673-902e-4f4c-ad02-5e753fbbd338/air-zoom-pegasus-36-trail-trail-running-shoe-Kb3t4l.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-dae14cc1-f793-47c5-9c05-9628f592e10e/air-zoom-pegasus-36-trail-trail-running-shoe-Kb3t4l.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-ce50a76b-9dbf-41f6-beb7-02f44aaabd0a/air-zoom-pegasus-36-trail-trail-running-shoe-Kb3t4l.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-02aefcc5-396b-40ae-a103-dd5dd0ff18e3/air-zoom-pegasus-36-trail-trail-running-shoe-Kb3t4l.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-6fea416d-aa6e-4af6-8b90-a8b4156fafaf/air-zoom-pegasus-36-trail-trail-running-shoe-Kb3t4l.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-00456107-7585-4aff-8292-dac693358e1d/air-zoom-pegasus-36-trail-trail-running-shoe-Kb3t4l.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AR5677-200', '2020-04-13 15:30:22', 4.60, 'Nike Air Zoom Pegasus 36 Trail', 109.95, 0.00, 'Nike', 'An icon trades pavement for path with the Nike Air Zoom Pegasus 36 Trail. A perforated mesh upper adds breathability, as double Zoom Air units underfoot cushion your stride. Opposing lugs on the outsole optimise traction on your uphill climbs.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/nbtzzncncskmnkzuqy9i/air-zoom-pegasus-36-trail-trail-running-shoe-t6RTpg.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/hj4cb3wpqwify0orsruv/air-zoom-pegasus-36-trail-trail-running-shoe-t6RTpg.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bn0qbvki3bqtksijk0ku/air-zoom-pegasus-36-trail-trail-running-shoe-t6RTpg.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ocoohwm03dfrixmx8wwj/air-zoom-pegasus-36-trail-trail-running-shoe-t6RTpg.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zaucben8g96guoeiqwmt/air-zoom-pegasus-36-trail-trail-running-shoe-t6RTpg.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/m3zaq18ua0o8hsnax2qo/air-zoom-pegasus-36-trail-trail-running-shoe-t6RTpg.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/rev3bkbtublp3qzpjz9t/air-zoom-pegasus-36-trail-trail-running-shoe-t6RTpg.jpg\"]', 1, 0);
