INSERT INTO `product` VALUES ('AR6631-004', '2020-04-13 15:30:20', 3.80, 'Nike Air VaporMax 2019', 127.97, 0.00, 'Nike', 'Designed for running but adopted by the street, the Nike Air VaporMax 2019 features the lightest, most flexible Air Max cushioning to date. A stretch woven material wraps your foot for lightweight support and stability, while an external reinforcement in the heel secures the back of your foot.', '[\"https://c.static-nike.com/a/images/t_PDP_1728_v1/vhkqfleqsr1arzdsajup/air-vapormax-2019-shoe-7382Lb.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/wudzyvmpj01ldzsvobey/air-vapormax-2019-shoe-7382Lb.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/yqnm1wyr6czaggwn6msm/air-vapormax-2019-shoe-7382Lb.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/zzr1k0vhtm87vyjkze9d/air-vapormax-2019-shoe-7382Lb.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/nzc109lsfwvyfpfci80a/air-vapormax-2019-shoe-7382Lb.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/uzy3gjywhl6ysb7hw6uk/air-vapormax-2019-shoe-7382Lb.jpg\"]', 1, 0);
