INSERT INTO `product` VALUES ('AR7410-603', '2020-04-13 15:37:01', 4.80, 'Nike Air Max Dia SE', 59.97, 0.00, 'Nike', 'The Nike Air Max Dia SE features sleek lines and a sheer upper that combine classic Air Max elements into a lightweight, comfy and versatile icon. Together with its smart toe-down profile and extra lift, the Nike Air Max Dia SE offers an ever-bigger expression of Nike Air.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/wjqt277boocmxve1lei6/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vyswutvscvzeckb3zmcl/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/beie5cn2aeaffwnwjvjt/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/qfj0ya5ngwzrx9acabqi/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/gnaifggagoyin9nlxdbb/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ljtcejf15x4vir83mo8f/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/kckvwfiv61nv2xhtp8jy/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/nme6v6vqrxsoca1zvfnp/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/np9ll0idqyon2flzbcac/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/pvdnsgxsrmwccemcydlm/air-max-dia-se-shoe-qF8H7r.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ckkgfb3x62qsc3pe2e2k/air-max-dia-se-shoe-qF8H7r.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AR7718-001', '2020-04-13 15:34:42', 3.40, 'Nike SB Zoom Stefan Janoski Canvas RM', 69.95, 0.00, 'Nike', 'The Nike SB Zoom Stefan Janoski Canvas RM pairs a minimal look with a modern fit. It has been updated with a new, plush sockliner and a flexible midsole for better boardfeel right out of the box.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/g16thmfkapl730glqgxh/sb-zoom-stefan-janoski-canvas-rm-skate-shoe-Q9bXgF.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/akernkftk85z5a3llt1k/sb-zoom-stefan-janoski-canvas-rm-skate-shoe-Q9bXgF.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/mlj0bsyxssgkoupfzxm6/sb-zoom-stefan-janoski-canvas-rm-skate-shoe-Q9bXgF.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/krfi8txhoss09yspt8xh/sb-zoom-stefan-janoski-canvas-rm-skate-shoe-Q9bXgF.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/jnxhhry8yhldycip2xzp/sb-zoom-stefan-janoski-canvas-rm-skate-shoe-Q9bXgF.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fojmeuluvssb1lhzn7at/sb-zoom-stefan-janoski-canvas-rm-skate-shoe-Q9bXgF.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AR8836-009', '2020-04-13 15:32:34', 0.00, 'NikeCourt Lite 2', 49.95, 0.00, 'Nike', 'The NikeCourt Lite 2 is designed to cushion and support your feet during training and play. Created specifically for hard courts, its unique outsole features five distinctive patterns. Each pattern is designed to visually outline 1 of the 5 basic movements new tennis players learn.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/rrf5ufweu5hzlqrrypdz/nikecourt-lite-2-hard-court-tennis-shoe-RzbD9h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/dqlye9nxxscobefdjpoi/nikecourt-lite-2-hard-court-tennis-shoe-RzbD9h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/pgvtyljyuy6n529rff0p/nikecourt-lite-2-hard-court-tennis-shoe-RzbD9h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/h0g672clgm46vqr86qpx/nikecourt-lite-2-hard-court-tennis-shoe-RzbD9h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/pkbbousbyyrybdmmsuia/nikecourt-lite-2-hard-court-tennis-shoe-RzbD9h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ai01btmoom4yxgl3k2x9/nikecourt-lite-2-hard-court-tennis-shoe-RzbD9h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zxgx3ouytlrsomvgh5g9/nikecourt-lite-2-hard-court-tennis-shoe-RzbD9h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/tehurgktqtlyquwspfbc/nikecourt-lite-2-hard-court-tennis-shoe-RzbD9h.jpg\"]', 1, 0);
