INSERT INTO `product` VALUES ('AT1198-401', '2020-04-13 15:28:17', 0.00, 'KD Trey 5 VII EP', 79.95, 0.00, 'Nike', 'Kevin Durant likes a shoe that feels broken-in straight away but still provides containment and support. The KD Trey 5 VII EP hits the ground running with a combination of bouncy cushioning with a precise, supportive fit that\'s ready to go right out of the box.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-43628759-0d10-4532-aa38-1d7688da5b54/kd-trey-5-vii-ep-shoe-HHVZxG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-9c621f29-77cb-43fb-9721-18dfa0e73294/kd-trey-5-vii-ep-shoe-HHVZxG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-bd780b04-e07d-4e5d-b487-ba33e2156dac/kd-trey-5-vii-ep-shoe-HHVZxG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-77519024-4100-461d-94a9-5b33fe969e90/kd-trey-5-vii-ep-shoe-HHVZxG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-5744db1a-bcba-4ab7-8a64-cc83106f24ea/kd-trey-5-vii-ep-shoe-HHVZxG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-fa67fbbc-c1c5-4738-8b28-7b7e1de9f6d4/kd-trey-5-vii-ep-shoe-HHVZxG.jpg\"]', 1, 0);
