INSERT INTO `product` VALUES ('AT3378-471', '2020-04-13 15:32:16', 4.20, 'Nike Air Max Alpha Savage', 89.95, 0.00, 'Nike', 'The Nike Air Max Alpha Savage invokes Air Max style, stability and comfort in a modern design. Webbing integrates with the laces for midfoot containment, while Max Air cushioning delivers responsive comfort during your most intense workouts.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/zdglmhgleyr182j5ucqm/air-max-alpha-savage-training-shoe-hJdbdr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/qclzn1osd3tisqzqjqsv/air-max-alpha-savage-training-shoe-hJdbdr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/gdwajnasfogctdq949pt/air-max-alpha-savage-training-shoe-hJdbdr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/qsfe3w2fehdmhkb7izpy/air-max-alpha-savage-training-shoe-hJdbdr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/adpcvcd1biyeir58ruou/air-max-alpha-savage-training-shoe-hJdbdr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vz9riv5nsitxa2e6qciy/air-max-alpha-savage-training-shoe-hJdbdr.jpg\"]', 1, 0);
