INSERT INTO `product` VALUES ('AV2605-101', '2020-04-13 15:28:16', 3.70, 'Nike React Presto', 59.97, 0.00, 'Nike', 'Inspired by the early 2000s original, the Nike React Presto puts an exaggerated spin on an unconventional icon. Nike React foam delivers an expressive look with a lightweight, bouncy feel and a whole lot of personality.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/cxgtm2clr22zesboufg1/react-presto-shoe-h5dC61.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/u35uuethj61tpcqralfx/react-presto-shoe-h5dC61.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fksrgvgnsgh8wplhabao/react-presto-shoe-h5dC61.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/x8d5hzxc2foacg4myun6/react-presto-shoe-h5dC61.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/gkflrde5irot5tuutwzd/react-presto-shoe-h5dC61.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/s79ktrwbpblvoh99pfje/react-presto-shoe-h5dC61.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/tmklaqfaxplffue1qxtl/react-presto-shoe-h5dC61.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AV3866-001', '2020-04-13 15:28:23', 5.00, 'Nike x Carhartt WIP Air Max 95', 169.95, 0.00, 'Nike', 'The Nike x Carhartt WIP Air Max 95 features reflective details and neon pops of colour. Carhartt WIP materials help construct the durable upper of a new take on the first sneaker with visible forefoot Nike Air.', '[\"https://c.static-nike.com/a/images/t_PDP_1728_v1/j1stymj9fjkwhybsmnll/carhartt-wip-air-max-95-shoe-3z31fN.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/hqaqrpnvtghiwxqrge1b/carhartt-wip-air-max-95-shoe-3z31fN.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/cap366wbpd4vbmqcbcxg/carhartt-wip-air-max-95-shoe-3z31fN.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/rohuoeg0kcur0vba8mnc/carhartt-wip-air-max-95-shoe-3z31fN.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/w5bs2nmtaphmscpbui4a/carhartt-wip-air-max-95-shoe-3z31fN.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/nlocszvwmdt0f5yygjk4/carhartt-wip-air-max-95-shoe-3z31fN.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/yukwkqoyhiuyz0ir7m0e/carhartt-wip-air-max-95-shoe-3z31fN.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/wvxvoqwyhx3rwm01eaba/carhartt-wip-air-max-95-shoe-3z31fN.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/vnjpxzwzmeukaji2asuz/carhartt-wip-air-max-95-shoe-3z31fN.jpg\",\"https://c.static-nike.com/a/images/t_PDP_1728_v1/emegphkf8ge82wmnrgxk/carhartt-wip-air-max-95-shoe-3z31fN.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('AV4052-116', '2020-04-13 15:36:36', 5.00, 'Air Jordan 1 Nova XX', 104.95, 0.00, 'Nike', 'Featuring an internal 1/2 sleeve construction, the Air Jordan 1 Nova XX Shoe offers a snug fit without traditional laces. Underfoot, an Air-Sole unit at the heel provides lightweight cushioning that moves with you all day long.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/dc3be887-0799-47a5-8d1d-552d9503e351/air-jordan-1-nova-xx-shoe-nQ94bb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a6d498ef-3149-452f-8a6a-48c2ee39d305/air-jordan-1-nova-xx-shoe-nQ94bb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/97f7551f-968f-43eb-87c6-eebb1291803d/air-jordan-1-nova-xx-shoe-nQ94bb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bffe375a-71d8-4976-aad7-ea79b0351230/air-jordan-1-nova-xx-shoe-nQ94bb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/0483fbc7-31c2-458c-a07c-ddf53e05b079/air-jordan-1-nova-xx-shoe-nQ94bb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ea272f82-e16f-4c2b-8d5d-5ddd28f0e329/air-jordan-1-nova-xx-shoe-nQ94bb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fe0ec7ca-ad74-4dbd-8302-a6ea210fc938/air-jordan-1-nova-xx-shoe-nQ94bb.jpg\"]', 1, 0);
