INSERT INTO `product` VALUES ('B22705', '2020-04-13 15:07:05', 0.00, 'UNISEX ADIDAS ORIGINALS SKATEBOARDING 3MC SHOES', 59.99, 0.00, 'Adidas', 'Combining skate-friendly design with a clean, classic profile, these shoes are in their element on or off the board. Their highly versatile style is rider-friendly, with a reinforced canvas upper and an ultra-flexible Geoflex outsole that moves naturally with your foot and has great board feel.', NULL, 1, 0);
