INSERT INTO `product` VALUES ('B22721', '2020-04-13 15:07:53', 0.00, 'MEN\'S ADIDAS ORIGINALS SKATEBOARDING CITY CUP SHOES', 75.99, 0.00, 'Adidas', 'These skate shoes are made of heavy-duty suede with a rubber heel wrap and reinforced toe to help resist blowouts. The breathable mesh lining is all about comfort, while the double-cup midsole and herringbone traction pattern extend up the sidewalls for maximum grip.', NULL, 1, 0);
