INSERT INTO `product` VALUES ('BD7914', '2020-04-13 15:07:30', 0.00, 'Men\'s adidas Originals FuturePacer Shoes', 199.99, 0.00, 'Adidas', 'The Futurepacer reworks the revolutionary design of the 1984 Micropacer shoes to challenge old ideas of what a sneaker should look like. This futuristic style makes a statement with a fully reflective stretchy upper and a mix of premium leathers. A Boost midsole with textured EVA plugs returns energy with every stride.', NULL, 1, 0);
