INSERT INTO `product` VALUES ('BQ3378-002', '2020-04-13 15:34:51', 3.00, 'Nike M2K Tekno', 71.97, 0.00, 'Nike', 'The Nike M2K Tekno Shoe draws inspiration from the Monarch franchise and pushes it into today with a futuristic heel counter and plush tongue for additional comfort.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/l0pygs73dsavdmfr7hnr/m2k-tekno-shoe-11FgvQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/rllaev8yb2ngzrkrl6c2/m2k-tekno-shoe-11FgvQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/nhvphtq2zmu6rtw81fja/m2k-tekno-shoe-11FgvQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vfeaghwuojdjfzez2fzy/m2k-tekno-shoe-11FgvQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/qtyoorkwksdeuoxlffxh/m2k-tekno-shoe-11FgvQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/rcvg8t76oeuxsm9j7nqw/m2k-tekno-shoe-11FgvQ.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ3381-600', '2020-04-13 15:28:05', 5.00, 'Air Jordan XXXIV PF', 149.95, 0.00, 'Nike', 'The Air Jordan XXXIV PF continues the legacy of a cultural icon. Light, responsive and sculpted with aerodynamic precision, it combines a boldly unconventional midsole with herringbone traction to merge innovative and iconic design elements.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-fc6dfc08-a6ab-4f81-9e57-ad8879ade6b3/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-7982a2e6-c6d6-4e67-9769-aef60115f468/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-034c839b-1ffb-40da-80bc-d146eef5aa20/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-ada00f22-3329-4c0f-af29-c9d7c07ea044/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-79cd3892-7891-43e1-b183-5678b4a00c91/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-32dd46be-8555-4d0c-aad7-b56677365a02/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-f6c563d4-7e3c-4495-914b-15f42a3b26aa/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-0474207a-315b-445b-9739-0a57530f3139/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-28adb96d-8e77-4d72-9e95-986154dd4201/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-faeff741-fa33-4f51-93d8-8dfaa788cf38/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-ce35c82d-4eaa-4c4d-8448-cdbce6812d36/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-38d9e696-9007-46bc-9a1a-5c0ec224c977/air-jordan-xxxiv-pf-basketball-shoe-bzvghf.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ3448-400', '2020-04-13 15:30:33', 5.00, 'Jordan Jumpman 2020 PF', 89.95, 0.00, 'Nike', 'The Jordan Jumpman 2020 PF delivers lightweight support for dynamic play on the court. A textile upper and secure midfoot webbing keep you moving freely during possessions.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/995b4d46-5a40-4e81-bd86-6a6f1c92d465/jordan-jumpman-2020-pf-basketball-shoe-CHhKGl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/8d5ba693-23d1-4187-9594-213c08b0a225/jordan-jumpman-2020-pf-basketball-shoe-CHhKGl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/6328a48e-5dea-496f-9778-dd3d7438daf5/jordan-jumpman-2020-pf-basketball-shoe-CHhKGl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/de496e1a-5388-4109-a41c-f04650eef20b/jordan-jumpman-2020-pf-basketball-shoe-CHhKGl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5f5c14f7-96d5-42f6-9e4a-af5c0893098c/jordan-jumpman-2020-pf-basketball-shoe-CHhKGl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/0f1bd2b4-1a01-4c3b-9cc9-8ff48c4a9657/jordan-jumpman-2020-pf-basketball-shoe-CHhKGl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cdd25776-1dda-4887-81fe-3250c5ddffdc/jordan-jumpman-2020-pf-basketball-shoe-CHhKGl.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ3610-100', '2020-04-13 15:32:44', 0.00, 'Nike Vandalised', 76.97, 0.00, 'Nike', 'The Nike Vandalised puts a totally new spin on the original 1984 Nike Vandal. Full of classic Nike b-ball swagger, the shoe\'s rearranged components give a bold new asymmetrical look.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/z2dupfxi3sr8o53vtxjp/vandalised-shoe-nlB62h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/tetsdxzoazxbjxv9wfh8/vandalised-shoe-nlB62h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/erfd0rdp0pkjosh99bov/vandalised-shoe-nlB62h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fkyimyoanwcusjuzvlns/vandalised-shoe-nlB62h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/gduktz7u6yt2rknoeepr/vandalised-shoe-nlB62h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/k5rno9no9y82ohdc2enf/vandalised-shoe-nlB62h.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/rirvi5mq9oim0virfrcf/vandalised-shoe-nlB62h.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ3611-100', '2020-04-13 15:32:44', 5.00, 'Nike Vandalised LX', 87.97, 0.00, 'Nike', 'The Nike Vandalised LX hacks Nike DNA for a totally new spin on the original 1984 Nike Vandal, a basketball classic. Rearranging all the components creates a bold new look with asymmetrical details.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/qom8hscypha8vrq3pixz/vandalised-lx-shoe-tch1gq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/eozw1ledb3izuo8u5fc7/vandalised-lx-shoe-tch1gq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vl6iffbqbduvmxqbuszz/vandalised-lx-shoe-tch1gq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/nv5ljnsuehll5n3za8zy/vandalised-lx-shoe-tch1gq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/agbqiq4jvmgxkd9mbspt/vandalised-lx-shoe-tch1gq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/n1ukzwvso6nb3cmgp2hj/vandalised-lx-shoe-tch1gq.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ4424-700', '2020-04-13 15:34:35', 5.00, 'Nike Air Force 1 \'07 Premium', 76.97, 0.00, 'Nike', 'The Nike Air Force 1 \'07 Premium captures the iconic look of its 1982 predecessor, with full-length cushioning for comfort and premium detailing that honours the original. A rich mix of materials gives an eye-catching look that\'s ready to hit the streets.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/admvrusbgd9wndkutlao/air-force-1-07-shoe-4hDbSc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/d1me3tdvto6vvztmdb5q/air-force-1-07-shoe-4hDbSc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/eoudlak7imvxgnipgma1/air-force-1-07-shoe-4hDbSc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vaj5ub3gdofsvrpty7ac/air-force-1-07-shoe-4hDbSc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/jh6reovam9b46yxagds3/air-force-1-07-shoe-4hDbSc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/qvrze3u4vqnlqaqb2pze/air-force-1-07-shoe-4hDbSc.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ4430-400', '2020-04-13 15:32:37', 5.00, 'Nike Air Max 720 Waves', 111.97, 0.00, 'Nike', 'Bring a new point of view to off-court culture in your Nike Air Max 720 Waves. Its low-profile stance showcases Nike\'s tallest Air unit to date while its D/MS/X design mixes culture with sport, letting you enter a fresh dimension of hoops style with unparalleled comfort.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/a4dgjpsmlvhj5o6iqtk1/air-max-720-waves-shoe-0kQXNG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bgwirgc43kq88g0ewvec/air-max-720-waves-shoe-0kQXNG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/s2wllwwsjbzrj20qa5iu/air-max-720-waves-shoe-0kQXNG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/un9jusrqtljbapmor3om/air-max-720-waves-shoe-0kQXNG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/eqjabylcqmlzng7s5f8q/air-max-720-waves-shoe-0kQXNG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/kvrnyanif3ndc3fihm4m/air-max-720-waves-shoe-0kQXNG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vphx0lbiyjgbh7oalymy/air-max-720-waves-shoe-0kQXNG.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ4630-500', '2020-04-13 15:32:25', 4.40, 'Kyrie 6 \'Enlightenment\'', 87.97, 0.00, 'Nike', 'In spiritual circles, the colour purple represents enlightenment. It speaks to being fully connected to your spiritual mind by tapping into an inner source of potential that was otherwise undiscovered. The Kyrie 6 \'Enlightenment\' plays up the unseen spirituality theme with an all-seeing eye graphic on the side of the upper and a purple colour scheme.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/cd6f7b5f-ad1d-48a8-b129-1f9d3f1bf72e/kyrie-6-enlightenment-basketball-shoe-1zgG18.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cb449591-d240-4124-b9e0-52e997ce1966/kyrie-6-enlightenment-basketball-shoe-1zgG18.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/1462f2f5-0e0a-453d-9609-f88332a7426b/kyrie-6-enlightenment-basketball-shoe-1zgG18.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a9041631-d8a4-4e5e-80c3-f2f5961b9b9f/kyrie-6-enlightenment-basketball-shoe-1zgG18.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/3cb97826-be60-45be-b837-f619d2ea242b/kyrie-6-enlightenment-basketball-shoe-1zgG18.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/db0a0959-9951-4d9c-9fb8-0e025b0c5e18/kyrie-6-enlightenment-basketball-shoe-1zgG18.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/030690c4-019c-4938-87dd-a76aa1b16bd0/kyrie-6-enlightenment-basketball-shoe-1zgG18.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ4631-100', '2020-04-13 15:34:24', 0.00, 'Kyrie 6 EP', 109.95, 0.00, 'Nike', 'Engineered for Kyrie Irving\'s unpredictable creativity on the court, the Kyrie 6 focuses on comfort, control and energy return to help him go fast and stay fresh. Bouncy cushioning is paired with soft yet supportive foam for great responsiveness and a smooth heel-to-toe transition. The midfoot strap and plush, padded collar lock him in and help keep him a step ahead of the competition.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-02ff7175-106d-4e3d-8be5-67c8c632f2fe/kyrie-6-ep-basketball-shoe-0tRPJ4.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-943d7a19-0db2-4124-97c6-0226bf77baee/kyrie-6-ep-basketball-shoe-0tRPJ4.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-a6a8e867-b417-4d5c-afa9-7cc3e2c60cf2/kyrie-6-ep-basketball-shoe-0tRPJ4.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-2025a441-fb8e-411e-8de3-ead091e1cf4b/kyrie-6-ep-basketball-shoe-0tRPJ4.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-65b4dbd4-5f32-492b-9a2e-0d20f8c9727d/kyrie-6-ep-basketball-shoe-0tRPJ4.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-1170cec4-6b06-40ec-9933-1d66f37f711c/kyrie-6-ep-basketball-shoe-0tRPJ4.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-a13ca316-cba8-4081-b41c-233433905746/kyrie-6-ep-basketball-shoe-0tRPJ4.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-378b347a-5bbd-4aae-b4f7-e135f2ba9504/kyrie-6-ep-basketball-shoe-0tRPJ4.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-57bc7e54-68c2-47dd-90a6-129ab845b486/kyrie-6-ep-basketball-shoe-0tRPJ4.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ4632-602', '2020-04-13 15:28:07', 4.30, 'Nike Offcourt', 24.95, 0.00, 'Nike', 'Featuring Revive Foam, a super-soft foam designed to help you relax and recharge, the unrelenting comfort of the Nike Offcourt Slide is sensational. The innovative dual-layered foam and soft jersey lining on the strap ensure a gratifying full-foot experience. The iconic Air Force 1 pivot circle outsole and block lettering on the strap casually nod to heritage hoops.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/bf29b8a2-b178-41dd-8bf5-113b6f838da6/offcourt-slide-kJDhS7.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cfcdfdf1-03d7-499f-81cc-f38ac4769f6c/offcourt-slide-kJDhS7.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/b9ec7692-65d0-4e06-af50-0a0c3a8429d6/offcourt-slide-kJDhS7.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a5d1f97f-f164-4d14-8f31-e324c230b3d9/offcourt-slide-kJDhS7.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a1eff022-927c-4ea9-a1fd-c3bac6465df7/offcourt-slide-kJDhS7.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ5102-400', '2020-04-13 15:32:23', 4.50, 'Nike Huarache Type', 65.97, 0.00, 'Nike', 'Part of the N354 family that nods to Steve Prefontaine\'s fastest mile, the Nike Huarache Type is based on one of the most legendary sneakers from Nike\'s All Conditions Gear line-up, the Nike Mowabb. Using an adjustable approach to the Huarache system, this shoe is an exploration of fit and comfort.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/myhnzwuzstzlyagpmbxb/huarache-type-shoe-6s9q8M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/qgvbhxk48iqtkgsejan8/huarache-type-shoe-6s9q8M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/un0yx0krwdp0sf9ycm6p/huarache-type-shoe-6s9q8M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/mfkudpflyfwawvnw08ic/huarache-type-shoe-6s9q8M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/gjecw4ehz8y4ihhzq5rv/huarache-type-shoe-6s9q8M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/pqpomxqslwcba6pjp92q/huarache-type-shoe-6s9q8M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/frntznxomfrp2x43coxr/huarache-type-shoe-6s9q8M.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ5190-101', '2020-04-13 15:32:28', 5.00, 'Nike Drop-Type Mid', 48.97, 0.00, 'Nike', 'Part of the N354 family that nods to Steve Prefontaine\'s fastest mile, the Nike Drop-Type Mid is inspired by classic tennis shoes in the NikeCourt family. With distinct design lines that borrow from the All Court 2, it puts a raw, experimental edge on an iconic look.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/tohghzwfreuerhbmki4z/drop-type-mid-shoe-T3Gb43.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a1vlk8x7rayeqqrknk4b/drop-type-mid-shoe-T3Gb43.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/qvvb2srvljztynvqqlis/drop-type-mid-shoe-T3Gb43.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/waz1eqpazoocrkwedfjb/drop-type-mid-shoe-T3Gb43.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/blgbdgztgsiliqetgnru/drop-type-mid-shoe-T3Gb43.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/l8akufzrxrrn2xqdknbp/drop-type-mid-shoe-T3Gb43.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/q79mhnivcwmxfua9v7hd/drop-type-mid-shoe-T3Gb43.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ5240-200', '2020-04-13 15:32:35', 4.80, 'Nike Xarr', 90.97, 0.00, 'Nike', 'The Nike Xarr sets you up to take on the elements in style. It\'s crafted with a water-resistant leather upper to help keep you dry and a rubber outsole to combat the season\'s wet conditions. With intricate detailing and a stylised tongue cover, it\'s a fresh new look with lots of attitude.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/x05mfzyewstlchz8vqzq/xarr-shoe-dQ53Wk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/twqxleidlnjkyfzewusx/xarr-shoe-dQ53Wk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/hhqcgqmxjvyjjlztl4ef/xarr-shoe-dQ53Wk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/t0azenmv7bfvnx765ezw/xarr-shoe-dQ53Wk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bj7ovpmcsj2jmt0zyxei/xarr-shoe-dQ53Wk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/uy0jcrohyd9u3vcxhbwd/xarr-shoe-dQ53Wk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zlxdmx4mucwqyoaoroml/xarr-shoe-dQ53Wk.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ5401-001', '2020-04-13 15:28:19', 2.90, 'Nike AlphaDunk', 111.97, 0.00, 'Nike', 'Join the hoops uprising in the AlphaDunk, one of Nike Basketball\'s bounciest game shoes to date. The revamped upper construction is light and supportive and a large cushioning unit spans nearly the entire forefoot, giving you all the responsiveness you can handle on the court.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/uymy5ecxo9izfbvylyiy/alphadunk-basketball-shoe-qh0qTV.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/horlh9g2mlgpidmrsaaz/alphadunk-basketball-shoe-qh0qTV.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/qa74rxptbqbijyva0bhv/alphadunk-basketball-shoe-qh0qTV.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/trprwcxy96bcnkwy2eiu/alphadunk-basketball-shoe-qh0qTV.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/rbq3sbti6bffx7svvro9/alphadunk-basketball-shoe-qh0qTV.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/uh2x3zzlnzkqk9rk63ip/alphadunk-basketball-shoe-qh0qTV.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ5422-200', '2020-04-13 15:30:23', 4.20, 'Zoom Freak 1', 99.95, 0.00, 'Nike', 'Get your freak on in Giannis Antetokounmpo\'s debut signature shoe, Nike\'s first-ever for the international basketball sensation. The Zoom Freak 1 has the extra-responsive cushioning, forefoot stability and multi-directional traction needed to complement his freakishly athletic game.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-20e6711d-6c38-469a-8362-8a45d0ce8423/zoom-freak-1-basketball-shoe-wxn8N0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-4ea9c8e1-b40f-4570-a2d6-40b8198a0535/zoom-freak-1-basketball-shoe-wxn8N0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-9a1165da-6638-48d0-8bfd-e269538fbb03/zoom-freak-1-basketball-shoe-wxn8N0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-4a68c22e-a4f4-4e78-a904-25b3af98a69b/zoom-freak-1-basketball-shoe-wxn8N0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-1392f569-09e9-4475-a0fe-886689f5890c/zoom-freak-1-basketball-shoe-wxn8N0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-8973239f-1ba8-42a9-84c7-50e7ad040c8a/zoom-freak-1-basketball-shoe-wxn8N0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-d9142a80-2e8a-4350-9d02-6480b3c42228/zoom-freak-1-basketball-shoe-wxn8N0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-4ed7b603-f509-42fa-9888-5e1dcbf01490/zoom-freak-1-basketball-shoe-wxn8N0.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ5427-401', '2020-04-13 15:28:26', 0.00, 'Nike Mercurial Superfly 7 Academy MDS MG', 89.95, 0.00, 'Nike', 'Dream of speed and play fast in the Nike Mercurial Superfly 7 Academy MDS MG. A sleek upper is streamlined for pace, while the versatile plate supercharges traction.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/ozfdohwam0p7cj0t1img/mercurial-superfly-7-academy-mds-mg-multi-ground-football-boot-th1FXb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/h428myf9yr0bqjdyprro/mercurial-superfly-7-academy-mds-mg-multi-ground-football-boot-th1FXb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zkhcavgjuqkk9mhe9riu/mercurial-superfly-7-academy-mds-mg-multi-ground-football-boot-th1FXb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/r4zxhq1rwt5k1cxta7zk/mercurial-superfly-7-academy-mds-mg-multi-ground-football-boot-th1FXb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/adw5ii27jbxwho6ivn3j/mercurial-superfly-7-academy-mds-mg-multi-ground-football-boot-th1FXb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fnnlb1fjwz7qnp5cdwpn/mercurial-superfly-7-academy-mds-mg-multi-ground-football-boot-th1FXb.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/hjrubrhil8zybyuqzk9c/mercurial-superfly-7-academy-mds-mg-multi-ground-football-boot-th1FXb.jpg\"]', 1, 0);
