INSERT INTO `product` VALUES ('BQ5467-101', '2020-04-13 15:36:32', 4.00, 'Nike Zoom Rise', 83.97, 0.00, 'Nike', 'Bounce like never before in the Zoom Rise, Nike Basketball\'s artfully crafted creation for the upwardly mobile. This mid-top provides containment and support up through the ankle, while an extra-large Nike Zoom Air unit under the ball of your foot redefines how responsive a shoe can be.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/atlpsqutgb0hjmljpxce/zoom-rise-basketball-shoe-xJvCBl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ea93mbmk0xvebcc6ypyh/zoom-rise-basketball-shoe-xJvCBl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/dne2zoffa3hoe7vfnnll/zoom-rise-basketball-shoe-xJvCBl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/xba8jfzinei8p0qnrofn/zoom-rise-basketball-shoe-xJvCBl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bualxvpo1rzckrnhaemt/zoom-rise-basketball-shoe-xJvCBl.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/oblhxxx7z0wqxva9i7vk/zoom-rise-basketball-shoe-xJvCBl.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ5469-703', '2020-04-13 15:32:30', 5.00, 'Nike Mercurial Superfly 7 Elite MDS FG', 249.95, 0.00, 'Nike', 'Dream of speed and play fast in the Nike Mercurial Superfly 7 Elite MDS FG. A streamlined upper combines with a Nike Aerowtrac zone for high-speed play and supercharged traction.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-e43bc0a3-290d-4620-983e-4d3d0a4688aa/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-436b914b-fbff-42a0-8346-99e6f7152545/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-9d80eff4-13de-4193-ad59-7186d03db11a/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-480edce9-431e-478a-b4fc-888d17cfe797/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-6597e2a9-4c4e-47bb-b25f-c0bbdbb2ec1b/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-cb50e780-409e-4e55-bce8-5288ba735e83/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-982331ef-6896-4a1c-94fe-fd5e74fe43fd/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-ebf9005d-07bf-4c72-a0ff-14d5277a3511/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-62d49aed-d6b6-4883-a512-6ee281bf64f0/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-92f8b107-74cb-45e1-9bf1-a1246ede7c6e/mercurial-superfly-7-elite-mds-fg-football-boot-F9QXKQ.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ5613-001', '2020-04-13 15:34:23', 0.00, 'Nike Air Max 98 NRG', 111.97, 0.00, 'Nike', 'The Nike Air Max 98 features the OG design lines that were inspired by the walls of the Grand Canyon, while adding bursts of fresh colour on the upper. The fluid, textured look and full-length Nike Air cushioning set you apart from the pack.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/50962ad8-6f1b-428b-a1bb-ecfe81905285/air-max-98-nrg-shoe-54DsfG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/75bb935b-0691-4153-935a-88a678dbece9/air-max-98-nrg-shoe-54DsfG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/15867c16-d5b7-4dc9-8a87-79753e48e1f3/air-max-98-nrg-shoe-54DsfG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/7ef6d546-d79f-4daf-9c48-a56355fb43c6/air-max-98-nrg-shoe-54DsfG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/1b668ef9-7a53-45da-afef-476ef2525472/air-max-98-nrg-shoe-54DsfG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/069a2cb6-ec0c-4ad4-b39a-7f427933f1a2/air-max-98-nrg-shoe-54DsfG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/510d3f1c-3874-4e3a-978d-b26e5f5cddbc/air-max-98-nrg-shoe-54DsfG.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/3eb82c5d-f92c-4977-acb7-19e90b77f864/air-max-98-nrg-shoe-54DsfG.jpg\"]', 1, 0);
