INSERT INTO `product` VALUES ('BQ5808-100', '2020-04-13 15:32:23', 0.00, 'Nike Air Max 720 Horizon', 159.97, 0.00, 'Nike', 'The aggressive traction pattern on the Nike Air Max 720 Horizon helps provide grip in wet conditions, while lightweight cushioning and an inner sleeve help keep you comfortable.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/wo2yvedg1p673krvewaw/air-max-720-horizon-shoe-KsTXCS.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/sgzdfkdq7u2ilnxun49s/air-max-720-horizon-shoe-KsTXCS.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/clanh4fqnwsxuslvlrgm/air-max-720-horizon-shoe-KsTXCS.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ggp9wqsonyyfafqreqku/air-max-720-horizon-shoe-KsTXCS.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bm5h4jz4unnuy0qdgy6n/air-max-720-horizon-shoe-KsTXCS.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/dykg601nxnx9at6i599d/air-max-720-horizon-shoe-KsTXCS.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vkjiuwusxmoqhfoac9qc/air-max-720-horizon-shoe-KsTXCS.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ5972-001', '2020-04-13 15:36:46', 0.00, 'Nike MX-720-818', 111.97, 0.00, 'Nike', 'Disrupt the universe in your Nike MX-720-818. Featuring materials inspired by spacesuits and quilted stitching on the upper, it takes the largest Air unit to date and blends it with the futuristic look of the final frontier. Mesh on the upper keeps your feet cool while the clear sole features the black and grey reminiscent of deep space.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/ylyjqvrvyecdvyj9whzu/mx-720-818-shoe-KW5gsr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/abcuzzjkzf13bijhvv9t/mx-720-818-shoe-KW5gsr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/oudtgyklox3n7shp0jya/mx-720-818-shoe-KW5gsr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/gse1fyd0eqm2axlsb8ii/mx-720-818-shoe-KW5gsr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/hzaeki96fcbxp4yunhjp/mx-720-818-shoe-KW5gsr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ac4iar2fgyncspn0pmwr/mx-720-818-shoe-KW5gsr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vl6eh9xeaji1asfxipoc/mx-720-818-shoe-KW5gsr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ybvdr5qr5x8ysvatr2rf/mx-720-818-shoe-KW5gsr.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/crjmtxzurnyam9jhwyjv/mx-720-818-shoe-KW5gsr.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BQ6044-100', '2020-04-13 15:32:38', 3.90, 'Nike React Metcon', 129.95, 0.00, 'Nike', 'The Nike React Metcon takes the stability and traction from the training original and pairs it with Nike\'s most comfortable cushioning. Tackle high-impact and lifting workouts with a light, breathable upper and durable grip to keep you going.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/ebcfdd35-e274-4ea6-87cb-bacbd9b2851f/react-metcon-training-shoe-bRkJ3L.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/de036c01-9e40-4ddf-8380-24266b0b4b55/react-metcon-training-shoe-bRkJ3L.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/aa57423c-5e2d-4e37-8ac0-74b921bb88cf/react-metcon-training-shoe-bRkJ3L.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/05b31fcc-098a-46f4-aaf7-bc4669853bfd/react-metcon-training-shoe-bRkJ3L.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ea1653fb-f4c1-46e1-b366-092e24cf2eec/react-metcon-training-shoe-bRkJ3L.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/3736dc89-d2bf-475d-a30b-c10c18e618fb/react-metcon-training-shoe-bRkJ3L.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5fe0337d-131e-4729-805a-a75fc6c1f3ae/react-metcon-training-shoe-bRkJ3L.jpg\"]', 1, 0);
