INSERT INTO `product` VALUES ('BV5841-800', '2020-04-13 15:32:36', 4.00, 'Nike MX-720-818', 159.95, 0.00, 'Nike', 'Disrupt the universe in your Nike MX-720-818. Featuring materials inspired by spacesuits and quilted stitching on the upper, it takes the largest Air unit to date and blends it with the futuristic look of the final frontier. Mesh on the upper keeps your feet cool while the clear sole features the black and grey reminiscent of deep space.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/0440fbf3-f4e4-4982-9df7-4eb3d13b48cc/mx-720-818-shoe-2WZpnW.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/0c064e7f-1b04-4771-b65e-27b0c28ff866/mx-720-818-shoe-2WZpnW.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/24578d4a-742b-4688-87f1-e88b857b29d7/mx-720-818-shoe-2WZpnW.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/1b31eef8-d2aa-46f2-a03c-03de936ad5f7/mx-720-818-shoe-2WZpnW.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/3e142ec6-388e-4028-9c38-1fa2ccf79dba/mx-720-818-shoe-2WZpnW.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/64ca7941-531c-4350-aeef-b98fa359db47/mx-720-818-shoe-2WZpnW.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/f203a91c-f6b2-4bc8-8bfe-35f48687b8e6/mx-720-818-shoe-2WZpnW.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e74b4a7a-f9df-425a-8a1f-3fb42e896acf/mx-720-818-shoe-2WZpnW.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/d3da3786-b8d4-4c34-aeca-dca8c06959b1/mx-720-818-shoe-2WZpnW.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BV6344-001', '2020-04-13 15:30:33', 3.70, 'Nike ACG React Terra Gobe', 77.97, 0.00, 'Nike', 'The Nike ACG React Terra Gobe combines rugged durability with ACG style to get you where you need to go in comfort. The textile and synthetic upper is breathable and durable, and Nike React technology in the midsole provides an extremely smooth and comfortable ride.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/b3danz4ulgomxm2wbrlr/acg-react-terra-gobe-shoe-dPNBmz.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/nlw6mu90n8p700gxxfrv/acg-react-terra-gobe-shoe-dPNBmz.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zlngjpg7vr8thumzs3rs/acg-react-terra-gobe-shoe-dPNBmz.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/dj5drfw5repmiy2nhnss/acg-react-terra-gobe-shoe-dPNBmz.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/n8hsf4wmvlc1fnenvpvd/acg-react-terra-gobe-shoe-dPNBmz.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/t8gms56biamni0yosejg/acg-react-terra-gobe-shoe-dPNBmz.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/izzv1wqcodvozgmthdwn/acg-react-terra-gobe-shoe-dPNBmz.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/kx6den5nea8mtvyrsnlp/acg-react-terra-gobe-shoe-dPNBmz.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BV6348-300', '2020-04-13 15:28:18', 2.60, 'Nike ACG Air Terra Antarktik', 159.95, 0.00, 'Nike', 'Designed and tested in Oregon, the Nike ACG Antarktik is a \"jacket\" for your foot. Its tough ripstop upper combines with a GORE-TEX lining to help keep your foot dry. All-new lug details use hard rubber for traction in all conditions.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/e18ea80d-da31-4518-8b9a-7e9da490d429/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e7ec3775-e979-4b6c-82a6-b21ad5f964d1/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e683e5a4-6817-48c7-a830-6ef4fb5e5061/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/55f7e23e-8862-4faa-ba50-045f18ec2097/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/d8e8607e-0e33-4416-8662-e0e9db29502b/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e6ec8c8b-f100-4ac0-b3e3-5c2ed80c11d0/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/aeefcba2-98ed-47f3-a526-74f5a97b9977/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5f003a8f-61ba-49e1-bbf8-507fd8406794/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/eb29c678-970d-4aff-a73f-cb70d2455938/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fe7e7ace-bafa-4c96-8af4-84c7efe1737c/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/d30948ae-5f7a-42b8-bd7e-9325a556beaa/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/52675533-87df-4b92-8657-268ca3aab3b7/acg-air-terra-antarktik-shoe-q2q0qj.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a28cbaf4-fbdc-4577-9c5c-c87dc40beb2e/acg-air-t