INSERT INTO `product` VALUES ('BV6484-002', '2020-04-13 15:36:48', 0.00, 'Nike Air Max 720 SE', 111.97, 0.00, 'Nike', 'Boasting Nike\'s tallest Air unit to date, the Nike Air Max 720 SE offers 360 degrees of cushioning and then some. With a jaw-dropping outsole and moulded upper, this shoe brings future style to the present day.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/7a18fee1-ee01-4903-8a13-791fc8cc1a7c/air-max-720-se-shoe-8fG2W3.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/9f08e759-6d74-4a6a-84a8-c870c18878fd/air-max-720-se-shoe-8fG2W3.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/f30bc4d2-ca00-48e0-af73-6e4bec0b6d15/air-max-720-se-shoe-8fG2W3.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/d36580d4-517d-4468-91e6-f875cdd3fc20/air-max-720-se-shoe-8fG2W3.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cab70bf8-4b7c-46f8-b333-491f0cce80c1/air-max-720-se-shoe-8fG2W3.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5486fc5a-3256-4cf0-9fe4-ca096eb00448/air-max-720-se-shoe-8fG2W3.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BV7099-400', '2020-04-13 15:36:48', 4.50, 'Nike Wildhorse 6', 99.95, 0.00, 'Nike', 'The Nike Wildhorse 6 keeps it stable, secure and lightweight on the trail. An updated outsole delivers the traction you need, while a foam midsole keeps you cushioned on your run.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-70e85685-d1fa-4ad9-a74c-ce1209adc64d/wildhorse-6-trail-running-shoe-5pX42x.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-b8e65353-87cf-4796-ab05-ccdc679667c4/wildhorse-6-trail-running-shoe-5pX42x.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-04880634-d13c-48b0-b766-f664d4da9802/wildhorse-6-trail-running-shoe-5pX42x.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-81d1af4a-0b77-4b18-a84f-5feeb1f4ffb0/wildhorse-6-trail-running-shoe-5pX42x.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-c92b0198-e540-4fd8-b00f-4bc215f4e3a6/wildhorse-6-trail-running-shoe-5pX42x.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-e245fd8c-ca52-4ead-930d-d267b56fc98d/wildhorse-6-trail-running-shoe-5pX42x.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-cb6e404f-cd3b-47b6-8140-a1cfebb2bfc4/wildhorse-6-trail-running-shoe-5pX42x.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-6dd454ab-fd52-4a00-8b35-2d7747757d3e/wildhorse-6-trail-running-shoe-5pX42x.jpg\"]', 1, 0);
