INSERT INTO `product` VALUES ('BV7725-500', '2020-04-13 15:28:19', 5.00, 'Nike Daybreak SP', 47.97, 0.00, 'Nike', 'The Nike Daybreak SP marks the return of the 1979 original, featuring suede and nylon on the upper and old-school graphics on the tongue for a \'70s runner-inspired look and feel.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/oo8nfaxjvklmg4hzh8gh/daybreak-sp-shoe-30r2Zk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/avctwk4jytyysoelu8ig/daybreak-sp-shoe-30r2Zk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/xomez0sfjzw6yj9dpdvo/daybreak-sp-shoe-30r2Zk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ke6euigjkc1wfyz8cfsl/daybreak-sp-shoe-30r2Zk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/y1wzqx17yxoy556g6tdu/daybreak-sp-shoe-30r2Zk.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/tsegm4guce9ko9roxd9o/daybreak-sp-shoe-30r2Zk.jpg\"]', 1, 0);
