INSERT INTO `product` VALUES ('BV7756-001', '2020-04-13 15:36:51', 5.00, 'Nike Zoom Fly 3 Premium', 104.97, 0.00, 'Nike', 'Inspired by the Vaporfly, the Nike Zoom Fly 3 gives distance runners race-day comfort and durability. The power of a carbon-fibre plate keeps you in the running mile after mile.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/j25auig79zd7qvcuxf2p/zoom-fly-3-running-shoe-Mcgswq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zo2yokamxarzo3zaro8g/zoom-fly-3-running-shoe-Mcgswq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cgj3jwjarkllkwn57atl/zoom-fly-3-running-shoe-Mcgswq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/mpsehujh9hb1xentscbd/zoom-fly-3-running-shoe-Mcgswq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/jzogazzhcmbegewksdcs/zoom-fly-3-running-shoe-Mcgswq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/eervrfaupzrq8uwq55bj/zoom-fly-3-running-shoe-Mcgswq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/vthnjzsatgxcd64wpsz9/zoom-fly-3-running-shoe-Mcgswq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/nhsdzlld9fvj5svljweg/zoom-fly-3-running-shoe-Mcgswq.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('BV7757-500', '2020-04-13 15:39:05', 5.00, 'Nike Zoom Pegasus Turbo 2 Special Edition', 127.97, 0.00, 'Nike', 'The Nike Zoom Pegasus Turbo 2 is updated with a featherlight upper, while energy-returning foam brings revolutionary responsiveness to your long-distance training.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/mannuvvxp0s1d63ucaze/zoom-pegasus-turbo-2-special-edition-running-shoe-tR3tf0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ehtyxm0ocrd9crklzkxj/zoom-pegasus-turbo-2-special-edition-running-shoe-tR3tf0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cqbplo1ooyezczafhaia/zoom-pegasus-turbo-2-special-edition-running-shoe-tR3tf0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/v09iu9eabzvctyjpbool/zoom-pegasus-turbo-2-special-edition-running-shoe-tR3tf0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/foxzrgcploi9ppbodpei/zoom-pegasus-turbo-2-special-edition-running-shoe-tR3tf0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/k09oeycpdblltofhy59s/zoom-pegasus-turbo-2-special-edition-running-shoe-tR3tf0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/kigco2x04gf19z9g3sd4/zoom-pegasus-turbo-2-special-edition-running-shoe-tR3tf0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ihkalj5krf2f7osetjcu/zoom-pegasus-turbo-2-special-edition-running-shoe-tR3tf0.jpg\"]', 1, 0);
