INSERT INTO `product` VALUES ('CD4363-103', '2020-04-13 15:30:34', 4.80, 'Nike Joyride Dual Run', 109.95, 0.00, 'Nike', 'The Nike Joyride Dual Run is designed to help make running feel easier and give your legs a day off. Tiny foam beads underfoot contour to your foot for cushioning that stands up to your mileage.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/8301e22c-db8f-49df-a004-b560dba2c547/joyride-dual-run-running-shoe-C3x99d.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c10843f7-99f8-4f91-b69c-72f5a0a336f3/joyride-dual-run-running-shoe-C3x99d.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/319e78ba-8706-48d2-a39a-283873d6b549/joyride-dual-run-running-shoe-C3x99d.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/f38af16a-d578-4285-8a7f-281b4f7664a1/joyride-dual-run-running-shoe-C3x99d.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e532c887-b997-4371-bc68-d83db1236a62/joyride-dual-run-running-shoe-C3x99d.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/de686bd8-e3eb-4c3a-964c-f1749a42aaf6/joyride-dual-run-running-shoe-C3x99d.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/4e77467c-03cd-47a8-a9f3-4980bb7036a4/joyride-dual-run-running-shoe-C3x99d.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/df53ce9f-b211-47e4-b50d-41ab8ff55416/joyride-dual-run-running-shoe-C3x99d.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/2e4b06be-2e20-4786-947f-78d4355cff5b/joyride-dual-run-running-shoe-C3x99d.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('CD4365-003', '2020-04-13 15:32:38', 4.30, 'Nike Joyride Dual Run', 76.97, 0.00, 'Nike', 'The Nike Joyride Dual Run blazes its own route. Tiny foam beads underfoot combined with traditional cushioning in the forefoot give an incredibly smooth feel that contours to your foot.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/c9cd4706-9814-4231-8c9d-cdcf19450fd5/joyride-dual-run-running-shoe-l272g9.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/b056c673-7267-49cd-ab10-2ce3681d92d5/joyride-dual-run-running-shoe-l272g9.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/4313cb8b-0d09-4fd3-b9b6-723b504d5dc1/joyride-dual-run-running-shoe-l272g9.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/6feadc79-90dc-433d-8623-d993ba0995f9/joyride-dual-run-running-shoe-l272g9.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/452bacdd-6e4d-4594-8b6f-f75373b8d5d1/joyride-dual-run-running-shoe-l272g9.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/82418bf7-0821-4faf-93d4-a7df4c638862/joyride-dual-run-running-shoe-l272g9.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cef935d4-d33d-4312-a24d-163f64e58441/joyride-dual-run-running-shoe-l272g9.jpg\"]', 1, 0);
