INSERT INTO `product` VALUES ('CD5050-400', '2020-04-13 15:28:20', 4.60, 'LeBron 17 Monstars', 169.95, 0.00, 'Nike', 'The Monstars have crash-landed in the Windy City, prepared to strip us of some of our greatest hardwood heroes. With the game in danger, who can Bugs and the Tune Squad turn to? Who other than The King himself, LeBron James, poised to dash the hopes and dreams of Mr Swackhammer and any team that stands in his way? The LeBron 17 Monstars is here just in time for All-Star in Chicago, where LeBron is ready to represent the Tune Squad and all that is great about the sport of basketball.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/989b4151-339f-4ec1-8558-08a2043d830f/lebron-17-monstars-basketball-shoe-XShGJ1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/af1b6c75-cd35-4384-b315-45cc08aab4ea/lebron-17-monstars-basketball-shoe-XShGJ1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/d0b518c9-f7e3-43a0-8f70-7b8469326440/lebron-17-monstars-basketball-shoe-XShGJ1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c877556c-48b5-4a57-8786-693cb948cf5c/lebron-17-monstars-basketball-shoe-XShGJ1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5e397c2e-0c4c-4012-ab5a-24c4d29c06be/lebron-17-monstars-basketball-shoe-XShGJ1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bad0c8f7-5749-44a7-9544-1ca7e1872638/lebron-17-monstars-basketball-shoe-XShGJ1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/b4dead5a-9f55-45a0-ace9-7d634002a4ab/lebron-17-monstars-basketball-shoe-XShGJ1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/20c0b420-9f12-4473-a8d5-d4a1739343e9/lebron-17-monstars-basketball-shoe-XShGJ1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c77f3c18-d74d-412a-83a7-63486eacb97f/lebron-17-monstars-basketball-shoe-XShGJ1.jpg\"]', 1, 0);
