INSERT INTO `product` VALUES ('CD6720-808', '2020-04-13 15:32:32', 0.00, 'Nike Mercurial Vapor 13 Elite SE FG', 219.95, 0.00, 'Nike', 'Building on the 360-degree Flyknit innovation of the 12, the Nike Mercurial Vapor 13 Elite SE FG adds a Nike Aerowtrac zone to the forefoot and a slightly stiffer chassis to help supercharge traction.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/gusclobq2h8gdlp4vvnb/mercurial-vapor-13-elite-se-fg-football-boot-XDNvKq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zankg4bfh6n99mcke2gy/mercurial-vapor-13-elite-se-fg-football-boot-XDNvKq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/x40fm1aoqzaxmxheaoat/mercurial-vapor-13-elite-se-fg-football-boot-XDNvKq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ejuqwh4nvhsskpwqnup7/mercurial-vapor-13-elite-se-fg-football-boot-XDNvKq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e7eckbq3ptrzdla2luqv/mercurial-vapor-13-elite-se-fg-football-boot-XDNvKq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zpp1yzhh4h7iav50jnyb/mercurial-vapor-13-elite-se-fg-football-boot-XDNvKq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/uhxe5vauxi9wl6b0egew/mercurial-vapor-13-elite-se-fg-football-boot-XDNvKq.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/h0t57agzhvwiad08usf4/mercurial-vapor-13-elite-se-fg-football-boot-XDNvKq.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('CD6721-808', '2020-04-13 15:28:18', 0.00, 'Nike Mercurial Superfly 7 Elite SE FG', 229.95, 0.00, 'Nike', 'Building on the 360-degree Flyknit innovation of the 6, the Nike Mercurial Superfly 7 Elite SE FG adds a Nike Aerowtrac zone to the forefoot and a slightly stiffer chassis to help super-charge traction. An insole with NikeGrip technology provides maximum interior traction underfoot and lightweight cushioning.', NULL, 1, 0);
INSERT INTO `product` VALUES ('CD6964-002', '2020-04-13 15:36:53', 4.00, 'Nike React Element 55 Premium', 71.97, 0.00, 'Nike', 'The Nike React Element 55 is a balanced blend of classic design and forward-looking innovation. Nike React foam delivers lightweight, bouncy comfort, while rubber outsole pods and an exaggerated outsole offer a cutting-edge look that begs a reaction.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/lzztwyeow1jqb2crwqp3/react-element-55-shoe-6fV7zs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/oq5unnrdxpl6mkj9qoov/react-element-55-shoe-6fV7zs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/v5l4kqgsm4ixlzceckmb/react-element-55-shoe-6fV7zs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/p8n0bmd6ufrsfcvhqi77/react-element-55-shoe-6fV7zs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/jl3xvd5ttfpk9rhdbuam/react-element-55-shoe-6fV7zs.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fleb9lq2cmiuawvwfmy2/react-element-55-shoe-6fV7zs.jpg\"]', 1, 0);
