INSERT INTO `product` VALUES ('CD7070-009', '2020-04-13 15:30:21', 4.60, 'Jordan Mars 270', 149.95, 0.00, 'Nike', 'Drawing inspiration from four classic Air Jordans, the Jordan Mars 270 is a modern-day remix that offers a heavy dose of heritage, plus an enormous Max Air unit and springy foam for non-stop comfort.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/92da4275-8da9-4b67-98a8-2f5f04706e5c/jordan-mars-270-shoe-Dv27m0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/4b73198b-a5c0-4f90-b7a8-22ab0a9ab391/jordan-mars-270-shoe-Dv27m0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/83ab8d51-6327-4b39-972a-cae641bd26bc/jordan-mars-270-shoe-Dv27m0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/95795801-f395-4e00-9ce9-7e0445c61b42/jordan-mars-270-shoe-Dv27m0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/7aa32d77-75b8-4b69-a2fe-9bafd3b7a1d3/jordan-mars-270-shoe-Dv27m0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/409b38c2-301e-43b4-8890-805399fc83e2/jordan-mars-270-shoe-Dv27m0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/066a60d1-1852-4fa8-979a-440d470e5b6d/jordan-mars-270-shoe-Dv27m0.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/f70e8626-21e7-420a-8667-19fa2c21c456/jordan-mars-270-shoe-Dv27m0.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('CD7287-003', '2020-04-13 15:36:44', 0.00, 'Nike Zoom Rival Fly', 47.97, 0.00, 'Nike', 'Inspired by Japan\'s Ekiden relay runners, the Nike Zoom Rival Fly features a futuristic look with a Zoom Air unit in the forefoot that gives you responsive propulsion. Flex grooves underneath optimise toe-off and soft foam cushions your stride. The mesh and synthetic upper blends strength and breathability.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/y8crsqhn2fnog8bpb3s7/zoom-rival-fly-running-shoe-gXV2M1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/atbfooes2oucgrkkqnjf/zoom-rival-fly-running-shoe-gXV2M1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/yo4ml3yiosgzt0ianfiz/zoom-rival-fly-running-shoe-gXV2M1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ezn6vnuuebqwtmdbxrqc/zoom-rival-fly-running-shoe-gXV2M1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/sw07swazdapziww8kdkw/zoom-rival-fly-running-shoe-gXV2M1.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/sblwb1llfxsom7fqekzo/zoom-rival-fly-running-shoe-gXV2M1.jpg\"]', 1, 0);
