INSERT INTO `product` VALUES ('CI1246-302', '2020-04-13 15:37:03', 3.70, 'Nike Air VaporMax SE', 101.97, 0.00, 'Nike', 'The Nike Air VaporMax SE wraps your foot in a stretch woven material that gives structure and support to your stride. Underfoot, the lightest, most flexible Air Max cushioning to date gives a super-bouncy sensation that rivals running on air.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/yciesifjyxhpgonpp1v4/air-vapormax-se-shoe-TWkwJx.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/h5utcylynxvqn7y3ugvj/air-vapormax-se-shoe-TWkwJx.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bpf2sijhcyb2icwkhgmj/air-vapormax-se-shoe-TWkwJx.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ryb8rpgcar00n7shsnsv/air-vapormax-se-shoe-TWkwJx.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fcptbwpaa8svspruwsvy/air-vapormax-se-shoe-TWkwJx.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/kxskxlxejsjbhito74zw/air-vapormax-se-shoe-TWkwJx.jpg\"]', 1, 0);
