INSERT INTO `product` VALUES ('CI1290-100', '2020-04-13 15:36:48', 5.00, 'Nike Epic Phantom React', 83.97, 0.00, 'Nike', 'Soft to the touch, the Nike Epic Phantom React lets go of laces for a slip-on style that gets you up and running in no time. A snug, secure, sock-like fit within a breathable Flyknit and synthetic upper feels as though the Phantom \"disappears\" as you run.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/awcgvayo4rwf4ovoz64m/epic-phantom-react-running-shoe-TGzpJw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/rxbmdmfyxwj7efjwvbsy/epic-phantom-react-running-shoe-TGzpJw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cos25pjh7mvdwekzwer7/epic-phantom-react-running-shoe-TGzpJw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/q4woj6geix9fqs4hsshi/epic-phantom-react-running-shoe-TGzpJw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/q8k4luv7saxsy2ikfbaw/epic-phantom-react-running-shoe-TGzpJw.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/xdp0h4293sc8kzb6mmuh/epic-phantom-react-running-shoe-TGzpJw.jpg\"]', 1, 0);
