INSERT INTO `product` VALUES ('CI3482-200', '2020-04-13 15:27:56', 0.00, 'Nike Air Force 1 Sage Low LX', 99.95, 0.00, 'Nike', 'Taking both height and craft to new levels, the Nike Air Force 1 Sage Low LX features a platform midsole and pared-down upper. Rolled edges and clean lines replace traditional overlays for a bold look.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/9c4f5787-cfca-49e1-ad6b-d8058da06a4c/air-force-1-sage-low-lx-shoe-nTkdbB.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/0adfe5c6-7948-4a79-ab0f-42afa8f669cd/air-force-1-sage-low-lx-shoe-nTkdbB.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ecc5d779-08f6-4874-9707-a5f390fe40ef/air-force-1-sage-low-lx-shoe-nTkdbB.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5367a63a-7c0f-41c0-ad75-3a6dc8b67518/air-force-1-sage-low-lx-shoe-nTkdbB.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/1e556acc-f95e-4051-b5e2-e462ee9edc89/air-force-1-sage-low-lx-shoe-nTkdbB.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/59a51ae0-7832-4b70-934b-a38e2d8db399/air-force-1-sage-low-lx-shoe-nTkdbB.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a222f074-a941-45d1-9d7d-f31b7d6e764a/air-force-1-sage-low-lx-shoe-nTkdbB.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/6c2dcb7d-fb1f-4aff-8d7f-060f33866cb4/air-force-1-sage-low-lx-shoe-nTkdbB.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('CI3703-401', '2020-04-13 15:28:19', 0.00, 'Nike Air Max 2 Light', 95.97, 0.00, 'Nike', 'Originally released in 1994, the Nike Air Max 2 Light returns as a near-exact replica of the iconic runner. A mesh and synthetic leather upper, moulded heel and vibrant Air Max unit honour the OG. The soft foam midsole optimises all-day comfort.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/e18b6d12-0e41-4697-8bb9-9e6410d0150e/air-max-2-light-shoe-r1kSJt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/85f278ed-2592-48ff-bad5-66b771c1db48/air-max-2-light-shoe-r1kSJt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/4b614b1b-8e16-49e7-a85d-629e5d15e988/air-max-2-light-shoe-r1kSJt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/fdc2d71a-cbfb-43bb-81ae-5ffd0504a99f/air-max-2-light-shoe-r1kSJt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e5df7003-f00b-490f-9f3c-8a168d57ec77/air-max-2-light-shoe-r1kSJt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/7b81dbbc-b821-4cbc-9774-364ce0134a8c/air-max-2-light-shoe-r1kSJt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/93e59b25-4098-4d7a-8f53-3dfb8e284e69/air-max-2-light-shoe-r1kSJt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/02a5a175-50a1-4157-87f0-095ce7f4133f/air-max-2-light-shoe-r1kSJt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5c8685f1-00fa-499c-97e6-0db684e48608/air-max-2-light-shoe-r1kSJt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/cebc7539-a35f-4361-8329-c29e1be6ea9c/air-max-2-light-shoe-r1kSJt.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('CI3705-600', '2020-04-13 15:30:26', 0.00, 'Nike Air Max 95 Essential', 111.97, 0.00, 'Nike', 'The Nike Air Max 95 Essential takes design cues from the human body. The midsole represents the spine, graduated panels are the muscles, the lace loops are the shoe\'s ribs and the mesh upper is the skin.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/361a1b3f-b1fc-4763-b04a-5f80d591310f/air-max-95-essential-shoe-6n9HLv.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a6260ec9-be92-4bfd-9a6b-1486bfaad7fb/air-max-95-essential-shoe-6n9HLv.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/17c2cff6-662d-41de-baf2-ddff2d5914a9/air-max-95-essential-shoe-6n9HLv.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e17dd54e-7fc0-4e15-b04a-9de4253ed6c7/air-max-95-essential-shoe-6n9HLv.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/708a0ac9-5e53-470d-82fc-574fa269497a/air-max-95-essential-shoe-6n9HLv.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c41f3e1b-935f-4d62-800f-d735c1410d90/air-max-95-essential-shoe-6n9HLv.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/0e1e3ca2-908f-4525-8e1e-09f3a6839a90/air-max-95-essential-shoe-6n9HLv.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c47f7837-a10a-4723-9f35-6e9c081e8173/air-max-95-essential-shoe-6n9HLv.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/df7b9c00-1f43-43be-8648-4d054775fb74/air-max-95-essential-shoe-6n9HLv.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e425d330-c59c-4750-8b89-adc807f6a779/air-max-95-essential-shoe-6n9HLv.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('CI3706-100', '2020-04-13 15:30:17', 0.00, 'Nike Joyride Flyknit AW', 119.97, 0.00, 'Nike', 'The Nike Joyride Run Flyknit AW is designed to help make running feel easier and give your legs a day off. Tiny foam beads underfoot contour to your foot for cushioning that stands up to your mileage.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/hguw6u4kljhfqursqjd9/joyride-flyknit-aw-running-shoe-zBGLbd.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/y5qtoyiyohk5ywqkq0ur/joyride-flyknit-aw-running-shoe-zBGLbd.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/s1dhpetschf3y3kmel0z/joyride-flyknit-aw-running-shoe-zBGLbd.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/tgweyhlsnlwkvgjp3yym/joyride-flyknit-aw-running-shoe-zBGLbd.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/etlqeufiebxidqm8xe6e/joyride-flyknit-aw-running-shoe-zBGLbd.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ub80n0sq6cjaxld0lo1j/joyride-flyknit-aw-running-shoe-zBGLbd.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/la5jrzlydpcsruiwkwas/joyride-flyknit-aw-running-shoe-zBGLbd.jpg\"]', 1, 0);
