INSERT INTO `product` VALUES ('CI3708-700', '2020-04-13 15:28:14', 5.00, 'Nike Air Max 97', 149.95, 0.00, 'Nike', 'The Air Max 97 shook up the running world with its revolutionary full-length Nike Air unit. The Nike Air Max 97 remasters the original design with bold new colours and flashes of gold.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/c40c635c-85a0-473d-8ecf-8621bfc8f1a1/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/3b780136-b033-4d40-b569-8ca9cecf4b5f/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5c6ad1d4-0896-41ce-9e35-f73a769f5f97/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/34540b1e-78bb-43cc-9aaf-f569da345d84/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/9145b037-85d6-4261-81ca-a87c14dcac78/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/124b5719-738f-4530-8e48-d347be13535c/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bfb84987-408c-4971-a5e3-c0b6a950d5fb/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/40d5746d-fa80-4380-ac80-626f3db7c36e/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/2814263b-b47c-4ecd-a8ee-b6b38b1a3506/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/3e105556-f8fb-402e-b5ed-eea999ab0daa/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/d5235c97-6557-4412-b7fd-e0b8170f4023/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/8f65d687-8efc-4136-a340-1b3d3370ae2e/air-max-97-shoe-cNRrFt.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/b62c50bb-c1c2-4391-9079-28fdc7c4c843/air-max-97-shoe-cNRrFt.jpg\"]', 1, 0);
INSERT INTO `product` VALUES ('CI3709-001', '2020-04-13 15:37:06', 4.00, 'Nike Air Max 98', 169.95, 0.00, 'Nike', 'The Nike Air Max 98 brings back retro Air Max style with contemporary comfort innovations. A full-length Max Air unit gives total cushioning in every step.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/007d8381-7f8b-443a-93c3-773ce74e377b/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e3ec2806-097b-44c5-80b8-fdfc3ef8f54e/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e192843d-54c0-407b-af74-4d6045161330/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/5e09009c-6188-4136-aeb8-554fbfce54de/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/6d5941b3-7e8d-4100-9db5-c008fdad5ff4/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/3c3a6aea-05a5-4f4f-8fde-461205ad8753/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/8083b90b-09a5-4d02-9a7f-082c703a1ed0/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/1dfe3368-ecdb-4cd6-8cf6-fb19fe29ca38/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/8d535ebf-6ae8-4206-b7b7-c3b3c11f6442/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c5fa9a41-1504-4bb0-b495-01862e02ad8a/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/a46d5388-1b2a-4b0b-812f-a0bdf584a760/air-max-98-shoe-lFP060.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/f0b3e794-0cad-4b9d-a477-6e17611fbabc/air-max-98-shoe-lFP060.jpg\"]', 1, 0);
