INSERT INTO `product` VALUES ('CJ0625-700', '2020-04-13 15:28:13', 0.00, 'Air Max 97 LX', 159.95, 0.00, 'Nike', 'Inspired by a high-speed bullet train, the Nike Air Max 97 LX brings back a running icon with a plush, lightweight upgrade. It modernises a retro design with a moulded upper, welded overlays and a Max Air unit for all-day comfort.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/fae7513b-cfff-4430-9575-752501bb54ba/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/b795c635-e48e-4177-bec8-63f52a55c1ed/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/f7918916-5366-48c8-a3b5-a6c345b6e498/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/e8877f3a-f121-4a10-ab0b-d7a33b0f9a45/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/abf47247-2c35-40b2-918c-bab8a497a447/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bb718340-fd00-409e-9956-0516c6d98fcb/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/eff558aa-0637-48c7-ba37-078646bcf71c/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/2e67298b-e6f8-4757-af80-0bcc5be3ef49/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c61590a7-9ffc-41d9-9bc1-22bccd7f7d59/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/51ec6637-d9eb-4d95-9b44-d87a8034760d/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/86081017-a924-481a-bf88-2e222ff6d532/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/bdf2279b-4ecb-4762-aa7a-ad107a43a142/air-max-97-lx-shoe-6V8gBm.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/c536e323-d4cc-4140-8be6-1dc2bac04917/air-max-97-lx-shoe-6V8gBm.jpg\"]', 1, 0);
