INSERT INTO `product` VALUES ('CK5669-400', '2020-04-13 15:28:19', 0.00, 'Nike Air Max 95 SP', 111.97, 0.00, 'Nike', 'Taking inspiration from the human body, the Nike Air Max 95 SP mixes unbelievable comfort with running DNA. The iconic side panels represent muscles, while Nike Air in the heel and forefoot cushions your every step.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/ig7d3fkjub5m248qujhy/air-max-95-sp-shoe-d76X5V.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/xcggsdvvpuzvdkhi5xny/air-max-95-sp-shoe-d76X5V.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/yixqkn4e7ywimb2srnle/air-max-95-sp-shoe-d76X5V.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ttsl40oq5wwz2wfojlqp/air-max-95-sp-shoe-d76X5V.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/zknar8tvzrixnddfmqja/air-max-95-sp-shoe-d76X5V.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/ic0wcem4mzi3gfwucdja/air-max-95-sp-shoe-d76X5V.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/jch07zof7rzcoyacgj3r/air-max-95-sp-shoe-d76X5V.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/jno86q2egh82dvpupeoh/air-max-95-sp-shoe-d76X5V.jpg\"]', 1, 0);
