INSERT INTO `product` VALUES ('CQ7731-900', '2020-04-13 15:34:21', 4.60, 'Nike Zoom KD12 \'YouTube\'', 103.97, 0.00, 'Nike', 'Breaking new ground for athletes, KD provides unprecedented access to his life through his YouTube channel. It\'s this type of authentic connection that KD wanted to create with his fans when he first partnered with YouTube in 2017. In celebration of his channel, the KD12 \'YouTube\' features co-branded graphic hits on the tongue and heel that celebrate two icons coming together to change the game.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/egxvpow065dlpacwwq4i/zoom-kd12-youtube-basketball-shoe-8w18p8.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/u2kdfhk0iszlqiz1jlso/zoom-kd12-youtube-basketball-shoe-8w18p8.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/jqszgi3h2hglyovqgncl/zoom-kd12-youtube-basketball-shoe-8w18p8.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/gi872paldaftvvmx1ewh/zoom-kd12-youtube-basketball-shoe-8w18p8.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/l39trvbomblel5rjnryc/zoom-kd12-youtube-basketball-shoe-8w18p8.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/hexx86awfzdstm6rjauj/zoom-kd12-youtube-basketball-shoe-8w18p8.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/xf8t8bnmrqd8tuaaazie/zoom-kd12-youtube-basketball-shoe-8w18p8.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/tdfzglsvdjulcgfu4wpo/zoom-kd12-youtube-basketball-shoe-8w18p8.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/kuvk8yotollh3n8ch0yf/zoom-kd12-youtube-basketball-shoe-8w18p8.jpg\"]', 1, 0);
