INSERT INTO `product` VALUES ('CT0624-100', '2020-04-13 15:34:55', 0.00, 'Nike Offcourt SE', 29.95, 0.00, 'Nike', 'The Nike Offcourt SE Slide features a premium suede upper with a modern Swoosh detail. The innovative foam and jersey lining ensure an effortlessly comfortable experience.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/1feafa93-e378-43d3-98e0-c0bb1c7715ac/offcourt-se-slide-Z5jr3M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/88daab96-95a4-4dd5-8a1b-28343a7b29e4/offcourt-se-slide-Z5jr3M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/94170b41-7524-41f0-9306-ee1428acb266/offcourt-se-slide-Z5jr3M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/6a27858e-0417-4b60-8eb7-45db859383dc/offcourt-se-slide-Z5jr3M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/78f7bd15-481f-4671-8a7c-35a4912ebbd0/offcourt-se-slide-Z5jr3M.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/7fe77331-9558-4e50-a2a0-96050bf05e01/offcourt-se-slide-Z5jr3M.jpg\"]', 1, 0);
