INSERT INTO `product` VALUES ('CT5064-001', '2020-04-13 15:34:35', 0.00, 'Nike Air Max 270 React', 139.95, 0.00, 'Nike', 'The Nike Air Max 270 React updates a classic with iridescent details, Nike React foam in the midsole and Max Air cushioning in the heel.', '[\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-59a74eed-676a-4672-b8de-23038563772a/air-max-270-react-shoes-GlJHqc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-9247eb38-d39c-48a8-9b64-1291056a7498/air-max-270-react-shoes-GlJHqc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-612525d4-51c5-4e54-9849-6a10072c3416/air-max-270-react-shoes-GlJHqc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-231edaa1-6a8c-4872-91e0-e36ed36ef21f/air-max-270-react-shoes-GlJHqc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-4b23a69b-3e3f-4584-8f2a-9791e8a54c19/air-max-270-react-shoes-GlJHqc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-4907d5d5-c059-4661-9430-7c1871a90f55/air-max-270-react-shoes-GlJHqc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-2a3499f4-84da-449b-aab5-369a42211c90/air-max-270-react-shoes-GlJHqc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-2b4b1eef-cbae-4bce-bac0-720029844706/air-max-270-react-shoes-GlJHqc.jpg\",\"https://static.nike.com/a/images/t_PDP_1728_v1/i1-6ddc09a3-2530-44a4-a83b-f8c22383d63e/air-max-270-react-shoes-GlJHqc.jpg\"]', 1, 0);
