INSERT INTO `product` VALUES ('D96931', '2020-04-13 15:07:59', 0.00, 'MEN\'S ADIDAS BASKETBALL MARQUEE BOOST LOW SHOES', 149.99, 0.00, 'Adidas', 'Updating a classic look with modern details, these low-cut basketball shoes are built for quickness and agility on the hardwood. They feature a moulded ankle collar with a cushioned heel for support and comfort. A responsive midsole returns energy on every jumper. The herringbone outsole provides extra grip as you stop and go on a dime.', NULL, 1, 0);
